  fetchCities() {
    const apiUrl = 'https://api.openaq.org/v1/cities?country_id=IN';
    this.http.get(apiUrl).subscribe((response: any) => {
      this.cities = response.results.map((result: any) => {
        return { name: result.city };
      });
    });
  }

}
