import { Component, OnInit } from '@angular/core';
import { IpcService } from '../ipc.service';
import { HttpClient } from '@angular/common/http';
import { Router } from '@angular/router';

@Component({
  selector: 'app-ask-question',
  templateUrl: './ask-question.component.html',
  styleUrls: ['./ask-question.component.css']
})
export class AskQuestionComponent implements OnInit  {

  cities:any
  areaOfLaw: string | undefined;
  questionTitle: string | undefined;
  questionText: string | undefined;
  myState: string | undefined;
  pinCode: number | undefined;
  myMobileNumber: number | undefined;
  agreeTerms: boolean | undefined;

  constructor(private ipcserv:IpcService){

  }
  ngOnInit(): void {
   this.ipcserv.getCities().subscribe((data:any)=>{
    this.cities=data.results;
    console.log(this.cities)
   })
  }

  submitForm() {

    console.log('Form submitted!');
    console.log('Area of Law:', this.areaOfLaw);
    console.log('Question Title:', this.questionTitle);
    console.log('Question Text:', this.questionText);
    console.log('My State:', this.myState);
    console.log('Pin Code:', this.pinCode);
    console.log('My Mobile Number:', this.myMobileNumber);
    console.log('Agree Terms:', this.agreeTerms);
  }
}
