import { Component, EventEmitter, Output } from '@angular/core';
import { IpcService } from '../ipc.service';

@Component({
  selector: 'app-ipc',
  templateUrl: './ipc.component.html',
  styleUrls: ['./ipc.component.css']
})
export class IpcComponent {
  ipc: any
   id:any
  // Display:any
  // constructor(private ser:IpcService){
  //    this.ipc=[]
  // }

  submitform(reg:any){
    this.id=reg.ipc
    //this.getIpc();
    this.getIpcByIpcCode(this.id)

  }
  // ngOnInit(): void {
  //   this.getIpcByIpcCode(this.ipc)
  // }


  // getIpc() {
  //   return this.ser.getipc().subscribe(
  //     (data: any) => {
  //       console.log(data);
  //       this.ipc = data;
  //     },
  //     (error: any) => {
  //       console.error('Error occurred:', error);
  //     }
  //   );
  // }


    ipcList: any[] = [];

    constructor(private ipcService: IpcService) {}

    ngOnInit(): void {
      this.getIpc();
    }

    getIpcByIpcCode(val: any) {
      return this.ipcService.getIpcById(val).subscribe(
        (data: any) => {
          this.ipc = data;
        },
        (error: any) => {
          console.error('Error occurred:', error);
        }
      );
    }

    getIpc(): void {
      this.ipcService.getipc().subscribe(
        (data: any[]) => {
          this.ipcList = data;
          //console.log(this.ipcList)
        },
        (error: any) => {
          console.error('Error retrieving IPC data:', error);
        }
      );
    }
  }
