import { Component, EventEmitter, Output } from '@angular/core';
import { IpcService } from '../ipc.service';

@Component({
  selector: 'app-ipc',
  templateUrl: './ipc.component.html',
  styleUrls: ['./ipc.component.css']
})
export class IpcComponent {
  ipc:any
  id:any
  Display:any
  constructor(private ser:IpcService){

  }
  submitform(reg:any){
    this.id=reg.ipc
    this.ser.getipc();
    //this.getIpcByIpcCode(this.id)

  }
  ngOnInit(): void {
    this.getIpcByIpcCode(this.ipc)
  }
  getIpcByIpcCode(val:any){
    return this.ser.getIpcById(val).subscribe((data:any)=>{
      this.ipc=data
    })
  }

  getIpc(){
    return this.ser.getipc().subscribe((data:any)=>{
      this.ipc=data
    })
  }
}
